import * as React from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import ButtonBase from '@mui/material/ButtonBase';
import Typography from '@mui/material/Typography';
import Link from "@mui/material/Link";
import {useState} from "react";
import DialogTitle from "@mui/material/DialogTitle";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import Button from "@mui/material/Button";
import DialogContent from "@mui/material/DialogContent";
import TextField from "@mui/material/TextField";
import FormControlLabel from "@mui/material/FormControlLabel";
import Checkbox from "@mui/material/Checkbox";
import {InputLabel} from "@mui/material";
import Grid from "@mui/material/Grid";

const images = [
    {
       url: '/calendar',
       title: 'Calendar',
       width: '30%',
    },
    {
       url: '/tours',
       title: 'Tours',
       width: '40%',
    },
    {
        url: '#',
        title: 'For You',
        width: '30%',
    },
];

const ImageButton = styled(ButtonBase)(({ theme }) => ({
           position: 'relative',
           height: 200,
           [theme.breakpoints.down('sm')]: {
           width: '100% !important', // Overrides inline-style
           height: 100,
       },
           '&:hover, &.Mui-focusVisible': {
           zIndex: 1,
           '& .MuiImageBackdrop-root': {
           opacity: 0.15,
       },
           '& .MuiImageMarked-root': {
           opacity: 0,
       },
           '& .MuiTypography-root': {
           border: '4px solid currentColor',
       },
       },
       }));

const ImageSrc = styled('span')({
           position: 'absolute',
           left: 0,
           right: 0,
           top: 0,
           bottom: 0,
           backgroundSize: 'cover',
           backgroundPosition: 'center 40%',
       });

const Image = styled('span')(({ theme }) => ({
           position: 'absolute',
           left: 0,
           right: 0,
           top: 0,
           bottom: 0,
           display: 'flex',
           alignItems: 'center',
           justifyContent: 'center',
           color: theme.palette.common.white,
       }));

const ImageBackdrop = styled('span')(({ theme }) => ({
           position: 'absolute',
           left: 0,
           right: 0,
           top: 0,
           bottom: 0,
           backgroundColor: theme.palette.common.black,
           opacity: 0.4,
           transition: theme.transitions.create('opacity'),
       }));

const ImageMarked = styled('span')(({ theme }) => ({
           height: 3,
           width: 18,
           backgroundColor: theme.palette.common.white,
           position: 'absolute',
           bottom: -2,
           left: 'calc(50% - 9px)',
           transition: theme.transitions.create('opacity'),
       }));



function HomePageComplexButtons() {
    const [isModalOpen, setModalOpen] = useState(false);
    const [selectedImage, setSelectedImage] = useState(null);

    const handleImageClick = (image) => {
        setSelectedImage(image);
        setModalOpen(true);
    };

    const handleCloseModal = () => {
        setModalOpen(false);
    };

    return (
        <Box sx={{ flexWrap: 'wrap', minWidth: 300, width: '100%' }}>
            {images.map((image) => (
                <a href={image.url}>
                    <ImageButton
                        focusRipple
                        onClick={() => {if(image.title === "For You") handleImageClick(image)}}
                        style={{
                            width: image.width,
                        }}
                    >
                        <ImageSrc style={{ backgroundImage: `url(${image.url})` }} />
                        <ImageBackdrop className="MuiImageBackdrop-root" />

                        <Image>
                            <Typography
                                component="span"
                                variant="subtitle1"
                                color="inherit"
                                sx={{
                                    position: 'relative',
                                    p: 4,
                                    pt: 2,
                                    pb: (theme) => `calc(${theme.spacing(1)} + 6px)`,
                                }}
                            >
                                {image.title}
                            </Typography>
                        </Image>
                    </ImageButton>
                </a>
            ))}
            <Quiz isOpen={isModalOpen} onClose={handleCloseModal} image={selectedImage} />
        </Box>
    );
}

export default HomePageComplexButtons;
