import React, { useEffect, useState } from "react";
import axios from "axios";
import { Button, Paper, TableRow, TableHead, TableContainer, TableCell, TableBody, Table } from "@mui/material";
import {Form, Modal} from "react-bootstrap";


function EventsTable() {

    const [events, setEvents] = useState([]);
    const [isOpen, setIsOpen] = useState(false);
    const [selectedRenter, setSelectedRenter] = useState(null);

    const openModal = (user) => {
        setIsOpen(true);
        setSelectedRenter(user);
    };

    const closeModal = () => {
        setIsOpen(false);
        setSelectedRenter(null);
    };


    const eventi = [{
        name:"Koncert Lepe Brene",
        location:"Plava voda",
        date:"29.2.2024",
        time:"20:00",
        details:"Najbolji"
    }];
    useEffect(() => {
        setEvents(eventi);
    }, []);

    function getEvents() {
        axios.get(`http://localhost:3005/admin/events/getEvents/`)
            .then((response) => {
                setEvents(response.data);
            })
            .catch((error) => {
                console.error('Error fetching fields:', error);
            });
    }
    function deleteEvent(id) {
        axios.delete(`http://localhost:3005/admin/events/deleteEvent/`)
            .then((response) => {
                setEvents(response.data);
            })
            .catch((error) => {
                console.error('Error deleting event:', error);
            });
    }
    function editEvent(id) {
        axios.put(`http://localhost:3005/admin/events/editEvent/`)
            .then((response) => {
                setEvents(response.data);
            })
            .catch((error) => {
                console.error('Error deleting event:', error);
            });
    }
    return (
        <div className="mt-5 box_shadow">
            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>#</TableCell>
                            <TableCell>Name</TableCell>
                            <TableCell>Location</TableCell>
                            <TableCell>Date</TableCell>
                            <TableCell>Time</TableCell>
                            <TableCell>Details</TableCell>
                            <TableCell>Actions</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {events.map((event, index) => (
                            <TableRow key={event.id}>
                                <TableCell>{index + 1}</TableCell>
                                <TableCell>{event.name}</TableCell>
                                <TableCell>{event.location}</TableCell>
                                <TableCell>{event.date}</TableCell>
                                <TableCell>{event.time}</TableCell>
                                <TableCell>{event.details}</TableCell>
                                <TableCell>
                                    <Button className="custom-button" onClick={editEvent(event.id)}>
                                        EDIT
                                    </Button>
                                    <Button className="custom-button" onClick={deleteEvent(event.id)}>
                                        DELETE
                                    </Button>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </div>
    );
}

export default EventsTable;
