import React, { useState } from "react";
import axios from "axios";
import { Navigate } from "react-router-dom";
import { Box, MenuItem, TextField, Button } from "@mui/material";
import {toast} from "react-toastify";

function addEventForm(props) {

    const [name, setName] = useState("");
    const [location, setLocation] = useState("");
    const [date, setDate] = useState("");
    const [time, setTime] = useState("");
    const [category, setCategory] = useState("");
    const [image, setImage] = useState(null);
    const [details, setDetails] = useState("");

    const notify = () => toast.success('Event created successfully!', {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "light",
    });

    function callFuns() {
        props.closeModal();
        toast.success('Event created successfully!', {
            position: "top-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "light",
        });
    }
    const handleKeyPress =(event) =>{

        if (event.key === 'Enter') {
            callFuns();
        }
    }

    return (
        <form encType="multipart/form-data" onKeyPress={handleKeyPress}>
            <Box mb={1}>
                <TextField
                    className="custom-input"
                    label="Name"
                    variant="outlined"
                    onChange={(e) => setName(e.target.value)}
                    inputProps={{ maxLength: 30 }}
                />
            </Box>
            <Box mb={1}>
                <TextField
                    className="custom-input"
                    variant="outlined"
                    label="Location"
                    onChange={(e) => setLocation(e.target.value)}
                />
            </Box>

            <Box mb={1}>
                <TextField
                    className="custom-input"
                    variant="outlined"
                    label="Details"
                    onChange={(e) => setDetails(e.target.value)}
                />
            </Box>

            <Box mb={1}>
                <TextField
                    className="custom-input"
                    type="file"
                    id="formBasicImg"
                    label="Image"
                    variant="outlined"
                    onChange={(e) => setImage(e.target.files)}
                    InputLabelProps={{ shrink: true }}
                />
            </Box>

            <Box textAlign="center">
                <Button className="custom-button mt-2" onClick={callFuns}>
                    {props.action}
                </Button>
            </Box>
        </form>
    );
}
export default addEventForm;
